import React, { ChangeEvent, KeyboardEvent } from 'react';

interface TodoInput {
  currentValue: string;
  onChange: (event: ChangeEvent<HTMLInputElement>) => void;
  onAdd: (event: KeyboardEvent<HTMLInputElement>) => void;
}

const Input: TodoInput = ({ currentValue, SetChange, SetAdd }) => {
  return (
    <div className="inputs">
      <input
        placeholder="What needs to be done?"
        value={currentValue}
        onChange={SetChange}
        onKeyDown={onAdd}
      />
    </div>
  );
};

export default Input;
