import React from 'react';

interface TodoFooter {
  activeCount: number;
  filter: 'all' | 'active' | 'completed';
  onFilterChange: (filter: 'all' | 'active' | 'completed') => void;
  onClearCompleted: () => void;
}

const Footer = ({
  activeCount,
  filter,
  onFilterChange,
  onClearCompleted,
}: TodoFooter) => {
  return (
    <div>
      <span>{activeCount} item left!</span>
      <span>
        <button onClick={() => onFilterChange('all')} className={filter === 'all' ? 'selected' : ''}>
          All
        </button>
        <button onClick={() => onFilterChange('active')} className={filter === 'active' ? 'selected' : ''}>
          Active
        </button>
        <button onClick={() => onFilterChange('completed')} className={filter === 'completed' ? 'selected' : ''}>
          Completed
        </button>
      </span>
      <button onClick={onClearCompleted}>Clear completed</button>
    </div>
  );
};

export default Footer;
