import React, { useReducer, SetStateAction } from "react";
import Input from "./input";
import List from "./list";
import Details from "./details";
import "./App.css";


interface State{
  data: [];
  currentValue: string;
  selected: [],
  all: boolean,
  active: boolean,
  completed: boolean,
}



const initialState:State = {
  data: [],
  currentValue: "",
  selected: [],
  all: true,
  active: false,
  completed: false,
};

function reducer(state: { data: State; selected: string | any[]; }, action: { type: any; payload: string; }) {
  switch (action.type) {
    case "SET_DATA":
      return { ...state, data: action.payload };
    case "SET_CURRENT_VALUE":
      return { ...state, currentValue: action.payload };
    case "SET_SELECTED":
      return { ...state, selected: action.payload };
    case "SET_FILTER":
      return {
        ...state,
        all: action.payload === "all",
        active: action.payload === "active",
        completed: action.payload === "completed",
      };
    case "CLEAR_COMPLETED":
      return {
        ...state,
        data: state.data.filter((item) => !state.selected.includes(item)),
        selected: [],
      };
    default:
      return state;
  }
}

function App() {
  const [state, dispatch] = useReducer(reducer, initialState);

  const handleAll = () => {
    dispatch({ type: "SET_FILTER", payload: "all" });
  };
  const handleActive = () => {
    dispatch({ type: "SET_FILTER", payload: "active" });
  };
  const handleCompleted = () => {
    dispatch({ type: "SET_FILTER", payload: "completed" });
  };
  const handleClearCompleted = () => {
    dispatch({ type: "CLEAR_COMPLETED" });
  };

  function setCurrentValue(value: SetStateAction<string>): void {
    throw new Error("Function not implemented.");
  }

  function setData(value: SetStateAction<string[]>): void {
    throw new Error("Function not implemented.");
