import React, { useState } from "react";
import "./App.css";

interface Todo {
  data: string;
  completed: boolean;
}

const Input: React.FC<{
  data: Todo[];
  setData: React.Dispatch<React.SetStateAction<Todo[]>>;
  currentValue: string;
  setCurrentValue: React.Dispatch<React.SetStateAction<string>>;
}> = ({ data, setData, currentValue, setCurrentValue }) => {
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setCurrentValue(event.target.value);
  };

  const handleAdd = (event: React.KeyboardEvent<HTMLInputElement>) => {
    if (event.key === "Enter" && currentValue.trim()) {
      setData([...data, { data: currentValue.trim(), completed: false }]);
      setCurrentValue("");
      lg
    }
  };

  return (
    <div className="inputs">
      <input
        placeholder="What needs to be done?"
        value={currentValue}
        onChange={handleChange}
        onKeyDown={handleAdd}
      />
    </div>
  );
};

const List: React.FC<{
  data: Todo[];
  all: boolean;
  active: boolean;
  completed: boolean;
  setData: React.Dispatch<React.SetStateAction<Todo[]>>;
}> = ({ data, all, active, completed, setData }) => {
  const handleChange = (item: Todo) => {
    setData((prev) =>
      prev.map((todo) =>
        todo.data === item.data ? { ...todo, completed: !todo.completed } : todo
      )
    );
  };

  const filteredData = data.filter((item) => {
    if (all) return true;
    if (active) return !item.completed;
    if (completed) return item.completed;
    return false;
  });

  return (
    <div>
      {filteredData.map((item, i) => (
        <div key={i}>
          <input
            type="checkbox"
            checked={item.completed}
            onChange={() => handleChange(item)}
          />
          <span>{item.data}</span>
          <button
            onClick={() => setData((prev) => prev.filter((todo) => todo.data !== item.data))}
          >
            X
          </button>
        </div>
      ))}
    </div>
  );
};

const Details: React.FC<{
  data: Todo[];
  all: boolean;
  active: boolean;
  completed: boolean;
  handleAll: () => void;
  handleActive: () => void;
  handleCompleted: () => void;
}> = ({ data, all, active, completed, handleAll, handleActive, handleCompleted }) => {
  const activeCount = data.filter((item) => !item.completed).length;

  return (
    <div>
      <span>{activeCount} item left!</span>
      <span>
        <button onClick={handleAll}>All</button>
        <button onClick={handleActive}>Active</button>
        <button onClick={handleCompleted}>Completed</button>
      </span>
      <button
      >
        Clear completed
      </button>
    </div>
  );
};

const App: React.FC = () => {
  const [data, setData] = useState<Todo[]>([]);
  const [currentValue, setCurrentValue] = useState("");
  const [all, setAll] = useState(true);
  const [active, setActive] = useState(false);
  const [completed, setCompleted] = useState(false);

  const handleAll = () => {
    setAll(true);
    setActive(false);
    setCompleted(false);
  };

  const handleActive = () => {
    setAll(false);
    setActive(true);
    setCompleted(false);
  };

  const handleCompleted = () => {
    setAll(false);
    setActive(false);
    setCompleted(true);
  };

  return (
    <div className="App">
      <h2>todos</h2>
      <Input
        data={data}
        setData={setData}
        currentValue={currentValue}
        setCurrentValue={setCurrentValue}
      />
      <List
        data={data}
        all={all}
        active={active}
        completed={completed}
        setData={setData}
      />
      <Details
        data={data}
        all={all}
        active={active}
        completed={completed}
        handleAll={handleAll}
        handleActive={handleActive}
        handleCompleted={handleCompleted}
      />
    </div>
  );
};

export default App;
