import React, { useContext } from "react";
import { ToDoListContext } from "./Main";



const Footer = () => {
  const context = useContext(ToDoListContext);
  const { Set_Completed, SetFilterChange } = context;
  const activeCount = context.todos.filter(todo => !todo.completed).length;

  return (
    <div>
      <span>{activeCount} item left!</span>
      <span>
        <button onClick={() => SetFilterChange('all')} >
          All
        </button>
        <button onClick={() => SetFilterChange('active')}>
          Active
        </button>
        <button onClick={() => SetFilterChange('completed')} >
          Completed
        </button>
      </span>
      <button onClick={Set_Completed}>Clear completed</button>
    </div>
  );
};

export default Footer;
