import { ChangeEvent, useReducer, KeyboardEvent } from "react";
import Input from "./refacter/Input";
import InputValue from "./refacter/InputValue";
import Details from "./refacter/Details";
import produce from "immer";

interface Todo {
  text: string;
  completed: boolean;
}

interface StateType {
  inputValue: Todo[] ;
  value: string;
  selected: string[];
  all: boolean;
  active: boolean;
  completed: boolean;
}

type Action =
  | { type: "ADD_ITEM" }
  | { type: "REMOVE_ITEM"; payload: string }
  | { type: "DELETE_SELECTED" }
  | { type: "TOGGLE_ITEM"; payload: string }
  | { type: "SHOW_ALL" }
  | { type: "SHOW_ACTIVE" }
  | { type: "SHOW_COMPLETED" }
  | { type: "SET_VALUE"; payload: string };

const initalState: StateType = {
  inputValue: [],
  value: "",
  selected: [],
  all: true,
  active: false,
  completed: false,
};

const  reducer = produce((state: StateType, action: Action) => {
  switch (action.type) {
    case "ADD_ITEM":
      state.inputValue = [
        ...state.inputValue,
        { text: state.value, completed: false },
      ];
      state.value = "";
      break;
    case "REMOVE_ITEM":
      state.inputValue = state.inputValue.filter(
        (item) => item.text !== action.payload
      );
      state.selected = [];
      break;

    case "DELETE_SELECTED":
      state.inputValue = state.inputValue.filter(
        (item) => !state.selected.includes(item.text)
      );
      state.selected = [];
      break;
    case "TOGGLE_ITEM":
      state.selected = state.selected.includes(action.payload)
        ? state.selected.filter((item) => item !== action.payload)
        : [...state.selected, action.payload];
      break;
    case "SHOW_ALL":
      state.all = true;
      state.active = false;
      state.completed = false;
      break;
    case "SHOW_ACTIVE":
      state.all = false;
      state.active = true;
      state.completed = false;
      break;
    case "SHOW_COMPLETED":
      state.all = false;
      state.active = false;
      state.completed = true;
      break;
    case "SET_VALUE":
      state.value = action.payload;
      break;
    default:
      return state;
  }
})

export default function Todolist() {
  const [state, dispatch] = useReducer(reducer, initalState);

  const addEvent = (event: ChangeEvent<HTMLInputElement>): void => {
    dispatch({ type: "SET_VALUE", payload: event.target.value });
  };

  const handleRemoveEvent = (element: string): void => {
    dispatch({ type: "REMOVE_ITEM", payload: element });
  };

  const handleDelete = (): void => {
    dispatch({ type: "DELETE_SELECTED" });
  };

  const handleCheckbox = (name: string): void => {
    dispatch({ type: "TOGGLE_ITEM", payload: name });
  };

  const handleAllShow = (): void => {
    dispatch({ type: "SHOW_ALL" });
  };

  const handleActiveShow = (): void => {
    dispatch({ type: "SHOW_ACTIVE" });
  };

  const handleCompletedShow = (): void => {
    dispatch({ type: "SHOW_COMPLETED" });
  };

  const handleAdd = (event: KeyboardEvent<HTMLInputElement>): void => {
    if (event.key === "Enter") {
      dispatch({ type: "ADD_ITEM" });
    }
  };

  return (
    <div>
      <Input value={state.value} addEvent={addEvent} handleAdd={handleAdd} />
      <InputValue
        value={state.value}
        all={state.all}
        inputValue={state.inputValue}
        selected={state.selected}
        handleCheckbox={handleCheckbox}
        handleRemoveEvent={handleRemoveEvent}
        active={state.active}
        completed={state.completed}
      />
      {state.inputValue.length !== 0 && (
        <Details
          inputValue={state.inputValue}
          selected={state.selected}
          handleAllShow={handleAllShow}
          handleActiveShow={handleActiveShow}
          handleCompletedShow={handleCompletedShow}
          handleDelete={handleDelete}
        />
      )}
    </div>
  );
}
