import React, { useReducer, useState } from "react";
import "./App.css";

interface State {
    data:[];
    currentValue: string,
    all: boolean;
    active:boolean,
    completed: boolean,
    datas: []

}
type Action = 
|{type:'Change_to_All'; payload: boolean }
|{type:'Change_to_Active'; payload: boolean }
|{type:'Change_to_Completed'; payload: boolean }

const initialState = {
    data: [],
    currentValue: '',
    all: true,
    active:false,
    completed: false,
    datas: []

}

const reducer = (state: State,action:Action):State => {
    switch(action.type){
        case 'Change_to_All':
            return {
                ...state ,
                all: action.payload ,
                active: !action.payload ,
                completed: !action.payload
            }
        case 'Change_to_Active':
                return {  
                    ...state ,
                    active: action.payload ,
                    all: !action.payload ,
                    completed: !action.payload
            }
         case 'Change_to_Completed':
                return {  
                    ...state ,
                    completed: action.payload ,
                    all: !action.payload ,
                    active: !action.payload
            }
        case "Change to HandleChange":
            return{
                ...state ,
                datas:       datas.includes(item) ? datas.filter((i) => i !== item) : [...datas, item]
            }

        }
    }



const Todolist: React.FC = () => {

    const [state , dispatch] = useReducer(reducer , initialState)
  const [data, setData] = useState<string[]>([]);
  const [currentValue, setCurrentValue] = useState("");
  const [all, setAll] = useState(true);
  const [active, setActive] = useState(false);
  const [completed, setCompleted] = useState(false);
  const [datas, setDatas] = useState<string[]>([]);

  const handleAll = () => {
    dispatch({type: 'Change_to_All' , payload: true})
  };

  const handleActive = () => {
    dispatch({type: 'Change_to_Active' , payload: true})
  };

  const handleCompleted = () => {
    dispatch({type: 'Change_to_Completed' , payload: true})
  };

  const handleChange = (item: string) => {
    dispatch({type:'Change to HandleChange'})
    setDatas((datas) =>
      datas.includes(item) ? datas.filter((i) => i !== item) : [...datas, item]
    );
  };

  const handleAdd = (event: React.KeyboardEvent<HTMLInputElement>) => {
    if (event.key === "Enter" && currentValue.trim()) {
      setData([...data, currentValue.trim()]);
      setCurrentValue("");
    }
  };

  const filteredData = data.filter((item) => {
    if (all) return true;
    if (active) return !datas.includes(item);
    if (completed) return datas.includes(item);
    return false;
  });

  return (
    <div className="Todolist">
      <h2>todos</h2>
      <div className="inputs">
        <input
          placeholder="What needs to be done?"
          value={currentValue}
          onChange={(e) => setCurrentValue(e.target.value)}
          onKeyDown={handleAdd}
        />
      </div>
      <div>
        {filteredData.map((item, i) => (
          <div key={i}>
            <input
              type="checkbox"
              checked={datas.includes(item)}
              onChange={() => handleChange(item)}
            />
            <span>{item}</span>
            <button
              onClick={() => setDatas((prev) => prev.filter((i) => i !== item))}
            >
              X
            </button>
          </div>
        ))}
      </div>
      <div>
        <span>{data.length - datas.length} item left!</span>
        <span>
          <button onClick={handleAll}>All</button>
          <button onClick={handleActive}>Active</button>
          <button onClick={handleCompleted}>Completed</button>
        </span>
        <button>Clear completed</button>
      </div>
    </div>
  );
};

export default Todolist;
