import React, { useState } from "react";
import Input from "./input";
import List from "./list";
import Details from "./details";
import "./App.css";

function App() {
  const [data, setData] = useState<string[]>([]);
  const [currentValue, setCurrentValue] = useState("");
  const [all, setAll] = useState(true);
  const [active, setActive] = useState(false);
  const [completed, setCompleted] = useState(false);
  const [selected, setDatas] = useState<string[]>([]);

  const handleAll = () => {
    setAll(true);
    setActive(false);
    setCompleted(false);
  };

  const handleActive = () => {
    setAll(false);
    setActive(true);
    setCompleted(false);
  };

  const handleCompleted = () => {
    setAll(false);
    setActive(false);
    setCompleted(true);
  };

  const handleClearCompleted = () => {
    setData((prevData) => prevData.filter((item) => !datas.includes(item)));
    setDatas([]);
  };

  return (
    <div className="App">
      <h2>todos</h2>
      <Input
        data={data}
        setData={setData}
        currentValue={currentValue}
        setCurrentValue={setCurrentValue}
      />
      <List
        data={data}
        datas={datas}
        setDatas={setDatas}
        setData={setData}
        all={all}
        active={active}
        completed={completed}
      />
      <Details
        data={data}
        datas={datas}
        handleAll={handleAll}
        handleActive={handleActive}
        handleCompleted={handleCompleted}
        handleClearCompleted={handleClearCompleted}
      />
    </div>
  );
}

export default App;
