import React, { useState } from "react";
import "./App.css";

const Input: React.FC<{
  data: string[];
  setData: React.Dispatch<React.SetStateAction<string[]>>;
  currentValue: string;
  setCurrentValue: React.Dispatch<React.SetStateAction<string>>;
}> = ({ data, setData, currentValue, setCurrentValue }) => {
  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setCurrentValue(event.target.value);
  };

  const handleAdd = (event: React.KeyboardEvent<HTMLInputElement>) => {
    if (event.key === "Enter" && currentValue.trim()) {
      setData([...data, currentValue.trim()]);
      setCurrentValue("");
    }
  };

  return (
    <div className="inputs">
      <input
        placeholder="What needs to be done?"
        value={currentValue}
        onChange={handleChange}
        onKeyDown={handleAdd}
      />
    </div>
  );
};

const List: React.FC<{
  data: string[];
  all: boolean;
  active: boolean;
  completed: boolean;
  datas: string[];
  setDatas: React.Dispatch<React.SetStateAction<string[]>>;
}> = ({ data, all, active, completed, datas, setDatas }) => {
  const handleChange = (item: string) => {
    setDatas((prev) =>
      prev.includes(item) ? prev.filter((i) => i !== item) : [...prev, item]
    );
  };

  const filteredData = data.filter((item) => {
    if (all) return true;
    if (active) return !datas.includes(item);
    if (completed) return datas.includes(item);
    return false;
  });

  return (
    <div>
      {filteredData.map((item, i) => (
        <div key={i}>
          <input
            type="checkbox"
            checked={datas.includes(item)}
            onChange={() => handleChange(item)}
          />
          <span>{item}</span>
          <button
            onClick={() => setDatas((prev) => prev.filter((i) => i !== item))}
          >
            X
          </button>
        </div>
      ))}
    </div>
  );
};

// Details Component
const Details: React.FC<{
  data: string[];
  datas: string[];
  handleAll: () => void;
  handleActive: () => void;
  handleCompleted: () => void;
}> = ({ data, datas, handleAll, handleActive, handleCompleted }) => {
  return (
    <div>
      <span>{data.length - datas.length} item left!</span>
      <span>
        <button onClick={handleAll}>All</button>
        <button onClick={handleActive}>Active</button>
        <button onClick={handleCompleted}>Completed</button>
      </span>
      <button>Clear completed</button>
    </div>
  );
};

// App Component
const App: React.FC = () => {
  const [data, setData] = useState<string[]>([]);
  const [currentValue, setCurrentValue] = useState("");
  const [all, setAll] = useState(true);
  const [active, setActive] = useState(false);
  const [completed, setCompleted] = useState(false);
  const [datas, setDatas] = useState<string[]>([]);

  const handleAll = () => {
    setAll(true);
    setActive(false);
    setCompleted(false);
  };

  const handleActive = () => {
    setAll(false);
    setActive(true);
    setCompleted(false);
  };

  const handleCompleted = () => {
    setAll(false);
    setActive(false);
    setCompleted(true);
  };

  return (
    <div className="App">
      <h2>todos</h2>
      <Input
        data={data}
        setData={setData}
        currentValue={currentValue}
        setCurrentValue={setCurrentValue}
      />
      <List
        data={data}
        datas={datas}
        setDatas={setDatas}
        all={all}
        active={active}
        completed={completed}
      />
      <Details
        data={data}
        datas={datas}
        handleAll={handleAll}
        handleActive={handleActive}
        handleCompleted={handleCompleted}
      />
    </div>
  );
};

export default App;
