import React from "react";

interface TodoInput {
  currentValue: string;
  onChange: (event: React.ChangeEvent<HTMLInputElement>) => void;
  onAdd: (event: React.KeyboardEvent<HTMLInputElement>) => void;
}

const Input:TodoInput = ({ currentValue, onChange, onAdd }) => {
  return (
    <div className="inputs">
      <input
        placeholder="What needs to be done?"
        value={currentValue}
        onChange={onChange}
        onKeyDown={onAdd}
      />
    </div>
  );
};

export default Input;
