import React, {createContext, useReducer, useState } from "react";
import "./App.css";
import Input from "./Inputs";
import ListDetail from "./ListDetail";
import Footer from "./Footer";
import produce from "immer";
import _ from 'lodash';


type Action =
  | { type: "Set_Add"; payload: string }
  | { type: "Set_Change"; payload: string }
  | { type: "Set_Delete"; payload: string }
  | { type: "Set_Filter"; payload: "all" | "active" | "completed" }
  | { type: "Set_Completed" }
  | { type: "Set_Filtered_Data", payload: Todo}
  | { type: "Set_Active_Count", payload: Number | Todo}

interface Todo {
  data: string;
  completed: boolean;
}

interface State {
  todos: Todo[];
  filter: "all" | "active" | "completed";
}

const initialState: State = {
  todos: [],
  filter: "all",
};

const reducer = produce((state: State, action: Action) => {
  switch (action.type) {
    case "Set_Add":
      state.todos = [
        ...state.todos,
        { data: action.payload, completed: false },
      ];
      break;

    case "Set_Change":
      state.todos = _.map(state.todos,(todo) =>
        todo.data === action.payload
          ? { ...todo, completed: !todo.completed }
          : todo);

      break;

    case "Set_Delete":
      state.todos = _.filter(state.todos,(todo) => todo.data !== action.payload);
      break;

    case "Set_Filter":
      state.filter = action.payload;
      break;

    case "Set_Completed":
      state.todos = _.filter(state.todos,(todo) => !todo.completed);
      break;

    case "Set_Filtered_Data":
      state.todos = _.filter(state.todos,(action) => {
          switch (action){
          case "all":
            return true;
          case "active":
            return !action.completed;
          case "completed":
            return action.completed;
          }
        }
      );
      break;

    case "Set_Active_Count":
      state.todos = _.filter(state.todos,(item) => !item.completed).length;
      break;
      
}});

const ToDoListContext = createContext();


const Main = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const [currentValue, setCurrentValue] = useState<string>("");

  const SetChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setCurrentValue(event.target.value);
  };

  const SetAdd = (event: React.KeyboardEvent<HTMLInputElement>) => {
    if (event.key === "Enter") {
      dispatch({ type: "Set_Add", payload: currentValue });
      setCurrentValue("");
    }
  };

  const SetTodoChange = (item: Todo) => {
    dispatch({ type: "Set_Change", payload: item.data });
  };

  const SetDelete = (item: Todo) => {
    dispatch({ type: "Set_Delete", payload: item.data });
  };

  const SetFilterChange = (filter: "all" | "active" | "completed") => {
    dispatch({ type: "Set_Filter", payload: filter });
  };
  const Set_Completed = () =>{
    dispatch({ type: "Set_Completed" })
  }
  const Set_Filtered_Data = (item: Todo) =>{
    dispatch({ type: "Set_Filtered_Data", payload: item });
  }
  const Set_Active_Count  = (item: Todo) =>{
    dispatch({ type: "Set_Active_Count", payload: item  })
  }

  // const filteredData = state.todos.filter((item) => {
  //   if (state.filter === "all") return true;
  //   if (state.filter === "active") return !item.completed;
  //   if (state.filter === "completed") return item.completed;
  //   return false;
  // });

  // const activeCount = state.todos.filter((item) => !item.completed).length;

  return (
    <div className="Main">
      <h2>todos</h2>
      <ToDoListContext.Provider value ={{
      ...state,
      currentValue,
      SetChange,
      SetAdd ,
      Set_Filtered_Data,
      SetTodoChange,
      SetDelete,
      Set_Active_Count,
      SetFilterChange,
      Set_Completed
      }}>
      {children}
    </ToDoListContext.Provider>

      {/* <Input
        currentValue={currentValue}
        SetChange={SetChange}
        SetAdd={SetAdd}
      />
      <ListDetail
        Set_Filtered_Data={Set_Filtered_Data}
        SetTodoChange={SetTodoChange}
        SetDelete={SetDelete}
      />
      <Footer
        Set_Active_Count={Set_Active_Count}
        onFilterChange={SetFilterChange}
        Set_Completed={Set_Completed}
      /> */}
    </div>
  );
};

export default Main;
