import React, { useReducer } from "react";
import "./App.css";

type Action =
  | { type: 'handle add'; payload: string }
  | { type: 'Handle_Change'; payload: string }
  | { type: 'DELETE_TODO'; payload: string }
  | { type: 'SET_FILTER'; payload: 'all' | 'active' | 'completed' }
  | { type: 'CLEAR_COMPLETED' };

interface Todo {
  data: string;
  completed: boolean;
}

interface State {
  todos: Todo[];
  filter: 'all' | 'active' | 'completed';
}

const initialState: State = {
  todos: [{ data: '', completed: false }],
  filter: 'all',
};

const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case 'handle add':
      return {
        ...state,
        todos: [
          ...state.todos,
          { data: action.payload, completed: false },
        ],
      };
    case 'Handle_Change':
      return {
        ...state,
        todos: state.todos.map(todo =>
          todo.data === action.payload
            ? { ...todo, completed: !todo.completed }
            : todo
        ),
      };
    case 'DELETE_TODO':
      return {
        ...state,
        todos: state.todos.filter(todo => todo.data !== action.payload),
      };
    case 'SET_FILTER':
      return {
        ...state,
        filter: action.payload,
      };
    case 'CLEAR_COMPLETED':
      return {
        ...state,
        todos: state.todos.filter(todo => !todo.completed),
      };
    default:
      return state;
  }
};

const Main: React.FC = () => {
  const [state, dispatch] = useReducer(reducer, initialState);
  const [currentValue, setCurrentValue] = React.useState("");

  const handleChange = (event: React.ChangeEvent<HTMLInputElement>) => {
    setCurrentValue(event.target.value);
  };

  const handleAdd = (event: React.KeyboardEvent<HTMLInputElement>) => {
    if (event.key === "Enter") {
      dispatch({ type: 'handle add', payload: currentValue });
      setCurrentValue("");
    }
  };

  const handleTodoChange = (item: Todo) => {
    dispatch({ type: 'Handle_Change', payload: item.data });
  };

  const handleDelete = (item: Todo) => {
    dispatch({ type: 'DELETE_TODO', payload: item.data });
  };

  const handleFilterChange = (filter: 'all' | 'active' | 'completed') => {
    dispatch({ type: 'SET_FILTER', payload: filter });
  };

  const filteredData = state.todos.filter(item => {
    if (state.filter === 'all') return true;
    if (state.filter === 'active') return !item.completed;
    if (state.filter === 'completed') return item.completed;
    return false;
  });

  const activeCount = state.todos.filter(item => !item.completed).length;

  return (
    <div className="Main">
      <h2>todos</h2>
      <div className="inputs">
        <input
          placeholder="What needs to be done?"
          value={currentValue}
          onChange={handleChange}
          onKeyDown={handleAdd}
        />
      </div>
      <div>
        {filteredData.map((item, i) => (
          <div key={i}>
            <input
              type="checkbox"
              checked={item.completed}
              onChange={() => handleTodoChange(item)}
            />
            <span>{item.data}</span>
            <button onClick={() => handleDelete(item)}>X</button>
          </div>
        ))}
      </div>
      <div>
        <span>{activeCount} item left!</span>
        <span>
          <button onClick={() => handleFilterChange('all')}>All</button>
          <button onClick={() => handleFilterChange('active')}>Active</button>
          <button onClick={() => handleFilterChange('completed')}>Completed</button>
        </span>
        <button onClick={() => dispatch({ type: 'CLEAR_COMPLETED' })}>
          Clear completed
        </button>
      </div>
    </div>
  );
};

export default Main;
