import React,{useState} from 'react';
import Input from './input';
import List from './list';
import Details from './details';
import './App.css';

function App() {
  const [data, setData] = useState<string[]>([]);
  const [currentValue, setCurrentValue] = useState("");
  const[all,setAll]=  useState(false);
  const[active,setActive]=  useState(false);
  const[completed,setCompled]=  useState(false);

const handleAll = () => {
  setAll(true);
  setActive(false);
  setCompled(false);
}
const handleActive = () => {
  setAll(false);
  setActive(true);
  setCompled(false);
}
const handleCompleted = () => {
  setAll(false);
  setActive(false);
  setCompled(true);
}

  return (
    <div className="App">
      <h2>todos</h2>
  <Input data={data} setData={setData} currentValue={currentValue} setCurrentValue={setCurrentValue}/>
  <List data={data} setData={setData} currentValue={currentValue} setCurrentValue={setCurrentValue} all={all} active={active} completed={completed}/>
  <Details  handleAll={handleAll}  handleActive={handleActive}  handleCompleted={handleCompleted}  />
    </div>
  );
}

export default App;
