import React from 'react';

interface ListProps {
  data: string[];
  all: boolean;
  active: boolean;
  completed: boolean;
  datas: string[];
  setDatas: React.Dispatch<React.SetStateAction<string[]>>;
}

const List: React.FC<ListProps> = ({ data, all, active, completed, datas, setDatas }) => {
  const handleChange = (item: string) => {
    setDatas((prev) => 
      prev.includes(item) ? prev.filter((i) => i !== item) : [...prev, item]
    );
  };

  const filteredData = data.filter((item) => {
    if (all) return true;
    if (active) return !datas.includes(item);
    if (completed) return datas.includes(item);
    return false;
  });

  return (
    <div>
      {filteredData.map((item, i) => (
        <div key={i}>
          <input 
            type="checkbox" 
            checked={datas.includes(item)} 
            onChange={() => handleChange(item)} 
          />
          <span>{item}</span>
          <button onClick={() => setData((prev) => prev.filter((i) => i !== item))}>X</button>
        </div>
      ))}
    </div>
  );
};

export default List;
